import React, { Component } from 'react';
import { Button, ButtonGroup } from 'reactstrap';


class Quiz extends Component {
    constructor(props) {
        super(props);
        this.state = {
            rSelected: null,
            Questions:[]
        };
    }

    componentDidMount() {
        // JSON dosyasından verileri alma işlemi
        fetch('./veriler.json')
          .then((response) => response.json())
          .then((veriler) => this.setState({ veriler }))
          .catch((error) => console.error('Veri alma hatası:', error));
      }

    handleRadioChange = (value) => {
        this.setState({ rSelected: value });
    }

    render() {
        const { rSelected } = this.state;

        return (
            <div>
                <section className='Sec-1'>
                    <p className='Quiz_Text bg-primary'>QUIZ 1</p>
                </section>
                <section className='Sec-2'>
                    <h2 className="animate__animated animate__bounce">An animated element
                        An animated element An animated element An animated element An animated element
                    </h2>
                </section>
                <section className='Sec-3'>
          
                        <ButtonGroup className='ButtonGroup'>
                            <Button
                                className='Item'
                         
                                onClick={() => this.handleRadioChange(1)}
                                active={rSelected === 1}
                            >
                                <label>A)</label>
                            </Button>
                            <Button
                                className='Item'
                      
                                onClick={() => this.handleRadioChange(2)}
                                active={rSelected === 2}
                            >
                                <label>B)</label>
                            </Button>
                            <Button
                                className='Item'            
                               
                                onClick={() => this.handleRadioChange(3)}
                                active={rSelected === 3}
                            >
                                <label>C)</label>
                                
                            </Button>
                        </ButtonGroup>
             
                </section>
            </div>
        );
    }
}

export default Quiz;
