import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

export default class Quiz extends Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedOption: 'A', // Sadece "A" seçeneği başlangıçta seçili
        };
    }

    handleOptionChange = (option) => {
        this.setState({
            selectedOption: option,
        });
    };

    render() {
        return (
            <div>
                <section className='Sec-1'>
                    <p className='Quiz_Text bg-primary'>QUIZ 1</p>
                </section>
                <section className='Sec-2'>
                    <h2 className="animate__animated animate__bounce">An animated element</h2>
                </section>
                <section className='Sec-3'>
                    <div>
                        <input
                            type="radio"
                            className="btn-check"
                            name="quizOptions"
                            id="option1"
                            autoComplete="off"
                            checked={this.state.selectedOption === 'A'}
                            onChange={() => this.handleOptionChange('A')}
                        />
                        <label className="btn btn-secondary" htmlFor="option1">A</label>
                    </div>

                    <div>
                        <input
                            type="radio"
                            className="btn-check"
                            name="quizOptions"
                            id="option2"
                            autoComplete="off"
                            checked={this.state.selectedOption === 'B'}
                            onChange={() => this.handleOptionChange('B')}
                        />
                        <label className="btn btn-secondary" htmlFor="option2">B</label>
                    </div>

                    <div>
                        <input
                            type="radio"
                            className="btn-check"
                            name="quizOptions"
                            id="option3"
                            autoComplete="off"
                            checked={this.state.selectedOption === 'C'}
                            onChange={() => this.handleOptionChange('C')}
                        />
                        <label className="btn btn-secondary" htmlFor="option3">C</label>
                    </div>

                    <div>
                        <input style={{backgroundColor:"red",width:"100px"}}
                            type="radio"
                            className="btn-check"
                            name="quizOptions"
                            id="option4"
                            autoComplete="off"
                            checked={this.state.selectedOption === 'D'}
                            onChange={() => this.handleOptionChange('D')}
                        />
                        <label className="btn btn-secondary" htmlFor="option4">D</label>
                    </div>
                </section>
            </div>
        )
    }
}
