import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

export default class Quiz extends Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedOption: 'A', // Sadece "A" seçeneği başlangıçta seçili
        };
    }

    handleOptionChange = (e) => {
        this.setState({
            selectedOption: e.target.value,
        });
    };

    render() {
        return (
            <div>
                <section className='Sec-1'>
                    <p className='Quiz_Text bg-primary'>QUIZ 1</p>
                </section>
                <section className='Sec-2'>
                    <h2 className="animate__animated animate__bounce">An animated element</h2>
                </section>
                <section className='Sec-3'>

                </section>
            </div>
        )
    }
}
