import React, { Component,useState } from 'react'
import { Button, ButtonGroup } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const [rSelected, setRSelected] = useState(null);
export default class Quiz extends Component {
    render() {
        return (
            <div>
                <section className='Sec-1'>
                    <p className='Quiz_Text bg-primary'>QUIZ 1</p>
                </section>
                <section className='Sec-2'>
                    <h2 class="animate__animated animate__bounce">An animated element
                        An animated element An animated element An animated element An animated element
                    </h2>
                </section>
                <section className='Sec-3'>
                    <div>
                        <ButtonGroup>
                            <Button
                                color="primary"
                                outline
                                onClick={() => setRSelected(1)}
                                active={rSelected === 1}
                            >
                                Radio 1
                            </Button>
                            <Button
                                color="primary"
                                outline
                                onClick={() => setRSelected(2)}
                                active={rSelected === 2}
                            >
                                Radio 2
                            </Button>
                            <Button
                                color="primary"
                                outline
                                onClick={() => setRSelected(3)}
                                active={rSelected === 3}
                            >
                                Radio 3
                            </Button>
                        </ButtonGroup>
                    </div>
                </section>
            </div>
        )
    }
}
