import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

export default class Quiz extends Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedOption: '', // Seçilen cevap
        };
    }

    handleOptionChange = (e) => {
        this.setState({
            selectedOption: e.target.value,
        });
    };

    render() {
        return (
            <div>
                <section className='Sec-1'>
                    <p className='Quiz_Text bg-primary'>QUIZ 1</p>
                </section>
                <section className='Sec-2'>
                    <h2 className="animate__animated animate__bounce">An animated element</h2>
                </section>
                <section className='Sec-3'>
                    <form>
                        <div className="radio">
                            <label>
                                <input
                                    type="radio"
                                    value="A"
                                    checked={this.state.selectedOption === 'A'}
                                    onChange={this.handleOptionChange}
                                />
                                A
                            </label>
                        </div>
                        <div className="radio">
                            <label>
                                <input
                                    type="radio"
                                    value="B"
                                    checked={this.state.selectedOption === 'B'}
                                    onChange={this.handleOptionChange}
                                />
                                B
                            </label>
                        </div>
                        <div className="radio">
                            <label>
                                <input
                                    type="radio"
                                    value="C"
                                    checked={this.state.selectedOption === 'C'}
                                    onChange={this.handleOptionChange}
                                />
                                C
                            </label>
                        </div>
                        <div className="radio">
                            <label>
                                <input
                                    type="radio"
                                    value="D"
                                    checked={this.state.selectedOption === 'D'}
                                    onChange={this.handleOptionChange}
                                />
                                D
                            </label>
                        </div>
                    </form>
                </section>
            </div>
        )
    }
}
